mapper.select:
SELECT COUNT(*)
FROM LECTURE_DATA
WHERE LECT_NO = #{detailCondition.lectNo}

SELECT B.* FROM( SELECT ROWNUM RNUM, A.* FROM( SELECT LD.LD_NO, LD.LD_TITLE, L.LECT_NAME, LD.LD_CONTENT, LD.LD_WDATE, ( SELECT COUNT(*) FROM ATCH_FILE WHERE LD_FILE = ATCH_ID ) ATCH_COUNT FROM LECTURE_DATA LD JOIN LECTURE L ON LD.LECT_NO = L.LECT_NO WHERE LD.LECT_NO = #{detailCondition.lectNo}
ORDER BY LD.LD_NO DESC ) A ) B
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}
